import React, { useEffect, useState } from 'react';
import EyesComponent from './EyesComponent';
import styled from 'styled-components';
import './App.css';

const App = () => {
  useEffect(() => {
    requestPermissions();
  }, []);

  const requestPermissions = () => {
    const permissions = [
      '카메라 접근 권한을 허용하시겠습니까?',
      '사운드 접근 권한을 허용하시겠습니까?',
      '정자세로 앉으면 정확도가 향상됩니다.',
    ];

    permissions.forEach((permission) => {
      alert(permission);
    });
  };

  return (
    <Container>
      <h1>캐-안습</h1>
      <EyesComponent />
      <Info>: 현대인의 올바른 컴퓨터 사용 습관 교정을 위한 눈 건강 보호 프로그램</Info>
      <ul>
        <li>
          <Checkmark />눈 움직임 파악을 위해 카메라 접근을 허용해주세요.
        </li>
        <li>
          <Checkmark />경고를 전달하기 위해 소리 접근을 허용해주세요.
        </li>
      </ul>
      <Cards>
        <Card title="눈 깜박임 경고" description="눈을 깜빡이는 횟수가 일정 이하로 줄어들면 경고창을 띄웁니다." />
        <Card title="눈 운동 애니메이션" description="컴퓨터를 장시간 사용하면 눈 건강을 위한 애니메이션을 띄웁니다." />
        <Card title="자동 밝기 조절" description="어두운 환경에서 컴퓨터를 할 경우 주변환경에 맞게 자동으로 밝기를 조절해줍니다." />
      </Cards>
    </Container>
  );
};

const Container = styled.div`
  text-align: center;
`;

const Info = styled.p`
  font-style: italic;
`;

const Checkmark = styled.span`
  color: green;
  margin-right: 5px;
`;

const Cards = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Card = ({ title, description }) => {
  return (
    <CardContainer>
      <CardHeader>
        <h2>{title}</h2>
        <ToggleSwitch isOn={false} />
      </CardHeader>
      <p>{description}</p>
      {title === "눈 깜박임 경고" && <CheckmarkBadge />}
    </CardContainer>
  );
};

const CardContainer = styled.div`
  border: 1px solid #ccc;
  border-radius: 5px;
  padding: 10px;
  margin: 10px;
  width: 300px;
`;

const CardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const CheckmarkBadge = () => {
  return (
    <CheckmarkContainer>
      <CheckmarkIcon />
      <span>경고 음성으로 받기</span>
    </CheckmarkContainer>
  );
};

const CheckmarkContainer = styled.div`
  display: flex;
  align-items: center;
`;

const CheckmarkIcon = styled.div`
  width: 16px;
  height: 16px;
  background-color: green;
  border-radius: 50%;
  margin-right: 5px;
`;

const ToggleSwitch = ({ isOn }) => {
  const [toggleState, setToggleState] = useState(isOn);

  const toggleSwitch = () => {
    setToggleState(!toggleState);
  };

  return (
    <ToggleSwitchStyled onClick={toggleSwitch} isOn={toggleState} />
  );
};

const ToggleSwitchStyled = styled.div`
  width: 40px;
  height: 20px;
  border-radius: 10px;
  cursor: pointer;
  transition: background-color 0.3s;
  position: relative;
  background-color: ${(props) => (props.isOn ? '#00cc00' : '#ccc')};

  &:after {
    content: '';
    width: 16px;
    height: 16px;
    background-color: #fff;
    border-radius: 50%;
    position: absolute;
    top: 2px;
    left: ${(props) => (props.isOn ? 'calc(100% - 18px)' : '2px')};
    display: block;
    transition: left 0.3s;
  }
`;

export default App;
