import React, { useEffect, useState } from 'react';
import EyesComponent from './EyesComponent';
import styled from 'styled-components';
import './App.css';

function App() {
  useEffect(() => {
    requestPermissions();
  }, []);

  const requestPermissions = () => {
    const permissions = [
      '카메라 접근 권한을 허용하시겠습니까?',
      '사운드 접근 권한을 허용하시겠습니까?',
      '정자세로 앉으면 정확도가 향상됩니다.',
    ];

    permissions.forEach((permission) => {
      alert(permission);
    });
  };

  return (
    <Container>
      <h1>캐-안습</h1>
      <EyesComponent />
      <InfoText>현대인의 올바른 컴퓨터 사용 습관 교정을 위한 눈 건강 보호 프로그램</InfoText>
      <ul>
        <li><Checkmark />눈 움직임 파악을 위해 카메라 접근을 허용해주세요. </li>
        <li><Checkmark />경고를 전달하기 위해 소리 접근을 허용해주세요.</li>
      </ul>
      <Card title="눈 깜박임 경고" description="눈을 깜빡이는 횟수가 일정 이하로 줄어들면 경고창을 띄웁니다." />
      <Card title="눈 운동 애니메이션" description="컴퓨터를 장시간 사용하면 눈 건강을 위한 애니메이션을 띄웁니다." />
      <Card title="자동 밝기 조절" description="어두운 환경에서 컴퓨터를 할 경우 주변환경에 맞게 자동으로 밝기를 조절해줍니다." />
    </Container>
  );
}

const Container = styled.div`
  margin: 0 auto;
  max-width: 800px;
  padding: 20px;
`;

const InfoText = styled.p`
  font-style: italic;
  font-size: 14px;
`;

const Checkmark = () => (
  <span role="img" aria-label="checkmark">
    ✔
  </span>
);

function Card({ title, description }) {
  return (
    <CardContainer>
      <CardHeader>
        <h2>{title}</h2>
        <ToggleSwitch isOn={false} />
      </CardHeader>
      <p>{description}</p>
      {title === "눈 깜박임 경고" && <CheckmarkBadge />}
    </CardContainer>
  );
}

const CardContainer = styled.div`
  background-color: #f4f4f4;
  padding: 10px;
  margin: 10px 0;
  border-radius: 8px;
`;

const CardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const CheckmarkBadge = () => (
  <div className="checkmark-badge">
    <div className="checkmark-icon"></div>
    <span>경고 음성으로 받기</span>
  </div>
);

export default App;
